package Loop;

public class For_loop
{

	public static void main(String[] args)
	{  
		int i;
		for(i=1;i<=20;i++)
		{
			System.out.println(" Increment Numers are "+i);
		}
		
		for(i=10;i>=1;i--)
		{
			System.out.println("Decrement number are "+i);
		}
		// TODO Auto-generated method stub

	}

}
