
package control_statement;

public class Switch_demo {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
        int year=4;
      switch(year)
      
      {
      case 1:System.out.println("welcome 1");
      break;
      case 2: System.out.println("welcome 2");
      break;
      case 3:System.out.println("welcome 3");
      break;
      case 4:System.out.println("welcome 4");
      break;
        
      default:System.out.println("not valid");
        
      break;
        
	}

}
}